import React from "react"
import { deleteCard } from "../../utils/api"


export default function DeckCards({deck, url, history}){
    async function deleteHandler(card){
        const abortController = new AbortController()
        const confirm = window.confirm("Are you sure you want to delete this deck? (Cannot be recovered)")
        if(confirm){
            deleteCard(card.id, abortController.signal)
            window.location.reload()

            return () => abortController.abort
        } else{
            history.push({url})
        }
    }

    const deckCards = deck.cards.map((card,index) => (
        <tbody key="start">
            <tr key={index}>
                <td>{card.front}</td>
                <td>{card.back}</td>
                <td><button type="button" onClick={()=>history.push(`${url}cards/${card.id}/edit`)}>Edit</button></td>
                <td><button type="button" onClick={() => deleteHandler(card)}>Delete</button></td>
            </tr>
        </tbody>
    ))

    return (
        <table>
            <thead>
                <tr>
                    <th>Cards:</th>
                </tr>
            </thead>
            <thead>
                <tr style={{fountSize: 20}}>
                    <td>Front:</td>
                    <td>Back:</td>
                </tr>
            </thead>
            {deckCards}
        </table>
    )
}
