import React from "react";
import { deleteDeck } from "../../utils/api";
import NavBar from "../Nav";

export default function ShowDeck({deck, url, history}){
    const {name, description, id} = deck
    const navigation = {Home: "/", [name]: ""}

    async function deleteHandler(id){
        const abortController = new AbortController()
        const confirm = window.confirm("Are you sure you want to delete this deck? (Cannot be recovered)")
        if(confirm){
            deleteDeck(id, abortController.signal)
            history.push("/")

            return () => abortController.abort()
        } else {
            history.push({url})
        }
    }
    return(
        <div>
            <NavBar
            navigation={navigation} />
            <h3>{name}</h3>
            <p>{description}</p>
            <button type="button" onClick={()=>history.push(`${url}edit`)}>Edit</button>
            <button type="button" onClick={()=>history.push(`${url}study`)}>Study</button>
            <button type="button" onClick={()=>history.push(`${url}cards/new`)}>Add Cards</button>
            <button type="button" onClick={()=>(deleteHandler(id))}>Delete</button>
        </div>
    )
}
