import React, {useEffect, useState} from "react"
import {Route, Switch, useParams, useRouteMatch, useHistory} from "react-router-dom"
import { readDeck } from "../../utils/api"
import DeckCards from "./DeckCards"
import ShowDeck from "./ShowDeck"

export default function Deck({decks}){
    const history = useHistory()
    const {deckId} = useParams()
    const {url, path} = useRouteMatch()
    const initialDeck = {name: "", description: "", id:{deckId}, cards: []}
    const {deck, setDeck} = useState(initialDeck)

    useEffect(() => {
        setDeck(initialDeck);
        const abortController = new AbortController();
        async function pullDeck() {
            try{
                const pullDeckOut = await readDeck(deckId, abortController.signal);
                setDeck(pullDeckOut);
            } catch (error) {
                if(error.name === "AbortError"){
                    console.log("Aborted");
                } else {
                    throw error;
                };
            };
        }
        pullDeck()


        return() => abortController.abort()
    }, [deckId])

    if(deck) {
        return (
            <div>
            <Switch>
                <Route exact={true} path={url}>
                    <ShowDeck 
                    deck={deck}
                    url={url} 
                    history={history} />

                    <DeckCards 
                    deck={deck}
                    url={url} 
                    history={history}/>
                </Route>
                <Route path={`${path}/study`}>
                    <StudyDeck 
                    deck={deck} />
                </Route>
                <Route path={`${path}/edit`}>
                    <EditDeck 
                    deck={deck}/>
                </Route>
                <Route path={`${path}/cards/new`}>
                    <AddCards 
                    deckName={deck.name} 
                    deckId={deckId} />
                </Route>
                <Route path={`${path}/cards/:cardId/edit`}>
                    <EditCard 
                    deckName={deck.name}
                    deckUrl={url}/>
                </Route>
            </Switch>
            </div>
        );
    } else {
       return <h3>.....Loading deck and cards</h3>
    }
};
