import React, {useState} from "react"
import {useHistory} from "react-router-dom"
import {createCard} from "../../utils/api"

export default function CardForm({deckId, card}) {
    const history = useHistory()
    const [newCard, setNewCard] = useState(card)

    const changeHandler = ({target}) => {
        setNewCard({
            ...newCard,
            [target.name]: target.value
        })
    }

    const submitHandler = (newCard, deckId) => {
        const abortController = new AbortController()
        const submittedCard = createCard(deckId, newCard, abortController.signal)
        setNewCard(submittedCard)
        history.push(`/decks/${deckId}/cards/new`)

        return () => abortController.abort()
    }

    return (
        <div>
            <form onSubmit={()=>submitHandler(newCard, deckId)}>
                <label htmlFor="front">
                    Front:
                </label>
                <textarea 
                    id="front"
                    name="front"
                    onChange={changeHandler}
                    placeholder={card.front}
                    required
                    value={card.front} />
                <label htmlFor="back">
                    Back:
                </label>
                <textarea
                    id="back"
                    name="back"
                    onChange={changeHandler}
                    required
                    value={card.back} />
                <hr />
                <button type="submit">Submit</button>
                <button type="button" onClick={history.push("/")}>Cancel</button>
            </form>
        </div>
    )
}
