import React from "react"
import { deleteDeck } from "../../utils/api"
import {useHistory} from "react-router-dom"


export default function DeckList({decks}){
    const history = useHistory()
    async function deleteHandle(deck){
        const abortController = new AbortController()
        const confirm = window.confirm("Are you sure you want to delete this deck? (Cannot be recovered)")

        if(confirm){
            deleteDeck(deck.id, abortController.signal());
            window.location.reload();

            return() => abortController.abort();
        } else {
            history.push("/")
        }
    }

    const deckList = decks.map((deck, index) => (
        <div key={index}>
            <h3>{deck.name}</h3>
            <h6>{deck.cards.length} cards in deck</h6>
            <p>{deck.description}</p>

            <button 
            type="button" 
            onClick={() => history.push(`/decks/${deck.id}/`)}>View</button>
            <button 
            type="button"
            onClick={() => history.push(`decks/${deck.id}/study`)}>Study</button>
            <button
            type="button"
            onClick={() => deleteHandle(deck.id)}>Delete</button>
        </div>
    ))

    return(
        <div>{deckList}</div>
    )
}
