import { useEffect, useState } from "react"
import { Link } from "react-router-dom";
import { deleteDeck } from "../utils/api";


export default function Home () {
    const [deck, setDeck] = useState([])

    useEffect(() => {
        async function loadDecks() {
            const response = await fetch("http://localhost:5000");
            const deckFromAPI = await response.json();
            setDeck(deckFromAPI)
        }
        loadDecks();
    }, [])

    if(deck.id) {
        return (
            <>
            <Link to="/decks/new">
                <button type="button" name="create-deck">+Create New Deck</button>
            </Link>
            <div>
                <h3>{deck.name}</h3>
                <p>{deck.description}</p>
                <Link to="/decks/:deckId">
                    <button type="button" name="view-deck">View</button>
                </Link>
                <Link to="/decks/:deckId/study">
                    <button type="button" name="study-deck">Study</button>
                </Link>
                <button type="button" name="delete-deck" onClick={deleteDeck}>Delete</button>
            </div>
            </>
        )
    }
}
